var d = new Date(msg.payload);

var hh = String(d.getHours()).padStart(2, '0');
var mm = String(d.getMinutes()).padStart(2, '0');

// dùng context để lưu (giờ nhận từ topic1, phút nhận từ topic2)
var hour = context.get("hour") || null;
var minute = context.get("minute") || null;

// phân biệt topic
if (msg.topic === "topic1") {
    hour = msg.payload;          // payload từ topic1 chứa GIỜ
    context.set("hour", hour);
}

else if (msg.topic === "topic2") {
    minute = msg.payload;        // payload từ topic2 chứa PHÚT
    context.set("minute", minute);
}

// so sánh giờ & phút
if (hour == hh && minute == mm) {
    msg.payload = 1;
} else {
    msg.payload = null;
}

return msg;
